-- UPSERT import (no TRUNCATE/DELETE). Updates/creates categories & items.
START TRANSACTION;

-- 1) Upsert categories (unique by name/code_category).
ON DUPLICATE KEY UPDATE `code_category`=VALUES(`code_category`), `updated_at`=VALUES(`updated_at`);

-- 2) Upsert items, binding category_id via category name.
/* Items upsert begin */
ON DUPLICATE KEY UPDATE `name`=VALUES(`name`), `details`=VALUES(`details`), `serial_number`=VALUES(`serial_number`), `procurement_year`=VALUES(`procurement_year`), `condition`=VALUES(`condition`), `category_id`=VALUES(`category_id`), `updated_at`=VALUES(`updated_at`);
/* Items upsert end */

COMMIT;